import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    full_name: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await register(formData);
      if (result.success) {
        navigate('/login');
      } else {
        setError(result.error || 'ការចុះឈ្មោះបរាជ័យ។ សូមព្យាយាមម្តងទៀត។');
      }
    } catch (err) {
      setError('បរាជ័យក្នុងការចុះឈ្មោះ។ សូមព្យាយាមម្តងទៀត។');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-50 to-purple-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <div className="mx-auto h-12 w-12 bg-indigo-600 rounded-full flex items-center justify-center">
            <span className="text-white font-bold text-xl">គ</span>
          </div>
          <h2 className="mt-6 text-center text-3xl font-bold text-gray-900 khmer-text">
            ចុះឈ្មោះគណនីថ្មី
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600 khmer-text">
            បង្កើតគណនីដើម្បីទិញសម្លៀកបំពាក់ពីហាងរបស់យើង
          </p>
        </div>

        <div className="bg-white shadow-lg rounded-lg p-8">
          <form className="space-y-6" onSubmit={handleSubmit}>
            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
                {typeof error === 'string' ? error : 'ការចុះឈ្មោះបរាជ័យ។ សូមព្យាយាមម្តងទៀត។'}
              </div>
            )}

            <div>
              <label htmlFor="username" className="block text-sm font-medium text-gray-700 khmer-text">
                �្មោះអ្នកប្រើ *
              </label>
              <input
                id="username"
                name="username"
                type="text"
                required
                value={formData.username}
                onChange={handleChange}
                className="mt-1 input-field"
                placeholder="បញ្ចូលឈ្មោះអ្នកប្រើ..."
              />
            </div>

            <div>
              <label htmlFor="full_name" className="block text-sm font-medium text-gray-700 khmer-text">
                �ំណើតពេញ *
              </label>
              <input
                id="full_name"
                name="full_name"
                type="text"
                required
                value={formData.full_name}
                onChange={handleChange}
                className="mt-1 input-field"
                placeholder="បញ្ចូលឈ្មោះពេញ..."
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 khmer-text">
                អ៊ីមែល *
              </label>
              <input
                id="email"
                name="email"
                type="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="mt-1 input-field"
                placeholder="បញ្ចូលអ៊ីមែល..."
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700 khmer-text">
                ពាក្យសម្ងាត់ *
              </label>
              <input
                id="password"
                name="password"
                type="password"
                required
                value={formData.password}
                onChange={handleChange}
                className="mt-1 input-field"
                placeholder="បញ្ចូលពាក្យសម្ងាត់..."
              />
            </div>

            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full btn-primary"
              >
                {loading ? 'កំពុងចុះឈ្មោះ...' : 'ចុះឈ្មោះ'}
              </button>
            </div>
          </form>

          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600 khmer-text">
              មានគណនីរួចហើយ?{' '}
              <Link to="/login" className="font-medium text-indigo-600 hover:text-indigo-500 khmer-text">
                ចូលប្រើប្រាស់
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
