import React, { useState, useEffect } from 'react';
import { useCart } from '../contexts/CartContext';
import { productsAPI } from '../services/api';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('全部');
  const [successMessage, setSuccessMessage] = useState('');
  const { addToCart } = useCart();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      console.log('Fetching products from API...');
      const response = await productsAPI.getProducts();
      console.log('API Response:', response.data);

      // Validate the products data
      const validProducts = response.data.filter(product => {
        if (!product || typeof product !== 'object') {
          console.warn('Invalid product:', product);
          return false;
        }
        return true;
      });

      console.log('Valid products:', validProducts);
      setProducts(validProducts);
    } catch (error) {
      console.error('Error fetching products:', error);
      console.error('Error details:', error.response?.data);
      // Set empty array on error to prevent crashes
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = (product) => {
    addToCart(product);
    setSuccessMessage(`បានបញ្ចូល "${product.name}" ក្នុងរទេះ!`);
    setTimeout(() => setSuccessMessage(''), 3000);
  };

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === '全部' || product.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const categories = ['全部', ...new Set(products.map(product => product.category).filter(Boolean))];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Debug Info */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <h3 className="text-sm font-semibold text-blue-800 mb-2">API Debug:</h3>
          <p className="text-xs text-blue-700">Products Count: {products.length}</p>
          <p className="text-xs text-blue-700">Loading: {loading ? 'Yes' : 'No'}</p>
          <button
            onClick={() => {
              console.log('Manual API test...');
              fetchProducts();
            }}
            className="mt-2 bg-blue-500 text-white px-3 py-1 rounded text-xs hover:bg-blue-600"
          >
            Test API Again
          </button>
        </div>

        {/* Success Message */}
        {successMessage && (
          <div className="bg-green-50 border border-green-200 text-green-700 px-4 py-3 rounded-lg mb-6 text-center">
            {successMessage}
          </div>
        )}

        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4 khmer-text">ផលិតផលទាំងអស់</h1>
          <p className="text-lg text-gray-600 khmer-text">ស្វែងរកសម្លៀកបំពាក់ដែលអ្នកចូលចិត្ត</p>
        </div>

        {/* Filters */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2 khmer-text">
                ស្វែងរកផលិតផល
              </label>
              <input
                type="text"
                placeholder="វាយបញ្ចូលឈ្មោះផលិតផល..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="input-field"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2 khmer-text">
                ប្រភេទ
              </label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="input-field"
              >
                {categories.map(category => (
                  <option key={category} value={category}>
                    {typeof category === 'string' ? category : 'ផ្សេងៗ'}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Products Grid */}
        {filteredProducts.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-500 khmer-text">រកមិនឃើញផលិតផលដែលអ្នកកំពុងស្វែងរកទេ</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {filteredProducts.map((product) => (
              <div key={product.id} className="product-card">
                {product.image_url ? (
                  <img
                    src={`http://localhost:8000${product.image_url.startsWith('/') ? '' : '/'}${product.image_url}`}
                    alt={product.name}
                    className="product-image"
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-500 text-2xl">រូប</span>
                  </div>
                )}
                <div className="product-info">
                  <h3 className="product-name khmer-text">
                    {typeof product.name === 'string' ? product.name : 'ផលិតផល'}
                  </h3>
                  <p className="product-price">
                    ${typeof product.price === 'number' ? product.price.toFixed(2) : '0.00'}
                  </p>
                  <p className="product-description khmer-text">
                    {typeof product.description === 'string' 
                      ? product.description.substring(0, 80) 
                      : 'គ្មានពិពណ៌នា'
                    }...
                  </p>
                  <div className="flex items-center justify-between mb-4">
                    <span className="text-sm text-gray-500 khmer-text">
                      ស្តុក: {typeof product.stock_quantity === 'number' ? product.stock_quantity : 0}
                    </span>
                    <span className="text-sm text-gray-500 khmer-text">
                      {typeof product.category === 'string' ? product.category : 'ផ្សេងៗ'}
                    </span>
                  </div>
                  <button
                    onClick={() => handleAddToCart(product)}
                    className="btn-primary w-full"
                    disabled={product.stock_quantity === 0}
                  >
                    {product.stock_quantity === 0 ? 'អស់ស្តុក' : 'បញ្ចូលក្នុងរទេះ'}
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Products;
