import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(formData);
      if (result.success) {
        navigate('/');
      } else {
        setError(result.error || 'ឈ្មោះអ្នកប្រើ ឬពាក្យសម្ងាត់មិនត្រឹមត្រូវ');
      }
    } catch (err) {
      setError('បរាជ័យក្នុងការចូលប្រើប្រាស់។ សូមព្យាយាមម្តងទៀត។');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-50 to-purple-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <div className="mx-auto h-12 w-12 bg-indigo-600 rounded-full flex items-center justify-center">
            <span className="text-white font-bold text-xl">គ</span>
          </div>
          <h2 className="mt-6 text-center text-3xl font-bold text-gray-900 khmer-text">
            ចូលប្រើប្រាស់គណនី
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600 khmer-text">
            ចូលប្រើប្រាស់ដើម្បីទិញសម្លៀកបំពាក់ពីហាងរបស់យើង
          </p>
        </div>

        <div className="bg-white shadow-lg rounded-lg p-8">
          <form className="space-y-6" onSubmit={handleSubmit}>
            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
                {typeof error === 'string' ? error : 'ឈ្មោះអ្នកប្រើ ឬពាក្យសម្ងាត់មិនត្រឹមត្រូវ'}
              </div>
            )}

            <div>
              <label htmlFor="username" className="block text-sm font-medium text-gray-700 khmer-text">
                �្មោះអ្នកប្រើ
              </label>
              <input
                id="username"
                name="username"
                type="text"
                required
                value={formData.username}
                onChange={handleChange}
                className="mt-1 input-field"
                placeholder="បញ្ចូលឈ្មោះអ្នកប្រើ..."
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700 khmer-text">
                ពាក្យសម្ងាត់
              </label>
              <input
                id="password"
                name="password"
                type="password"
                required
                value={formData.password}
                onChange={handleChange}
                className="mt-1 input-field"
                placeholder="បញ្ចូលពាក្យសម្ងាត់..."
              />
            </div>

            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full btn-primary"
              >
                {loading ? 'កំពុងចូល...' : 'ចូល'}
              </button>
            </div>
          </form>

          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600 khmer-text">
              មិនទាន់មានគណនី?{' '}
              <Link to="/register" className="font-medium text-indigo-600 hover:text-indigo-500 khmer-text">
                ចុះឈ្មោះឥឡូវនេះ
              </Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
